import { Component, Input } from '@angular/core';
import { Alarm } from '../app.model';
import { AlarmService } from '../data.service';

@Component({
  selector: 'app-edit-popup',
  templateUrl: './edit-popup.component.html',
  styleUrls: ['./edit-popup.component.css']
})
export class EditPopupComponent {
  @Input() selectedAlarm: Alarm | null = null;
  isEditPopupOpen: boolean = false;

  constructor(private alarmService: AlarmService) { }

  editAlarm(): void {
    if (!this.selectedAlarm) {
      console.error('No alarm selected for editing.');
      return;
    }

    this.alarmService.updateAlarm(this.selectedAlarm).subscribe(
      () => {
        console.log('Alarm updated successfully:', this.selectedAlarm);
        this.closeEditPopup();
      },
      error => {
        console.error('Error updating alarm:', error);
        // Optionally, display an error message to the user
      }
    );
  }

  closeEditPopup(): void {
    this.isEditPopupOpen = false;
  }
}
